var link=document.querySelector(".btn-map"),popup=document.querySelector(".modal"),close=document.querySelector(".close"),form=popup.querySelector("form"),name=form.querySelector("[name=name]"),email=form.querySelector("[name=e-mail]");link.addEventListener("click",function(a){a.preventDefault(),console.log("Открыть модальное окно"),popup.classList.add("modal-show")}),close.addEventListener("click",function(a){a.preventDefault(),console.log("Закрыть модальное окно"),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(a){name.value||email.value||(a.preventDefault(),form.classList.add("form-invalid"),console.log("Заполните пустые поля"))});